# Script backup và restore dữ liệuparam(    [string]$Mode,    [string]$BackupPath)$foldersToBackup = @(    "$env:USERPROFILE\AppData",    "$env:USERPROFILE\Documents",    "$env:USERPROFILE\Downloads",    "$env:USERPROFILE\LocalAppData")if ($Mode -eq "backup") {    if (Test-Path $BackupPath) {        Remove-Item $BackupPath -Force    }    Compress-Archive -Path $foldersToBackup -DestinationPath $BackupPath -CompressionLevel Optimal}elseif ($Mode -eq "restore") {    if (Test-Path $BackupPath) {        Expand-Archive -Path $BackupPath -DestinationPath "C:\" -Force    }}